import React from 'react';
import CommentsContainer from '../CommentsContainer';
import { postsData } from '../postsData';

export default function SinglePost({ postData: { id, heading, description, likes_count, liked, comments } }) {

    return (
        <div>
            <h2>{heading}</h2>
            <p>{description}</p>
            <p>likes:{likes_count}</p>
            {postsData.map(el => <CommentsContainer key={el.id} {...el} />)}
        </div>
    );
}
