import React from 'react';

export default function ProductCard({ title, price, imgUrl, id, discount }) {

    // Приведение к числу
    const numericPrice = Number(price).toFixed(2);
    const numericDiscount = Number(discount).toFixed(2);

    const newPrice = numericPrice * (1 - numericDiscount / 100);

    return (
        <div>
            <p>Discount: {discount}%</p>
            <p>Title: {title}</p>
            <p>Original price: {price}$</p>
            <p>New price is {newPrice ? newPrice : 'Invalid price or discount'}$</p>
            <img src={imgUrl} alt={title} />
            <a href={`https://yandex.ru/images/search?isize=small&text=${title}%20fruit`}>Link</a>
            <p>ID: {id}</p>
        </div>
    );
}
